	// }
	return (
		<div className='product'>
			<div className='product_info'>
				<p>{title}</p>
				<p className='product_price'>
				<small>$</small>
				<strong>{price}</strong>
				</p>
				<div className='product_rating'>
					{Array(rating)
						.fill()
						.map((_, i) => (
						<p>⭐️</p>
					))
					}
				</div>
			</div>
			<img src={image} alt="" />
			<button onClick={console.log("тут")}>Add to busket</button>
		</div>
	)
}

export default Product;
