export const initialState = {
    basket:[]
};

//Selector
export const getBasketTotal = (basket) => basket?.reducer((amount,item))

const reducer = (state,action) => {
    console.log(action);
    switch (action.type) {
        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: [...state.basket,action.item]
            };
        default:
            return state;
    }
};

export default reducer;
